INSERT INTO `sms_tpl` (`sms_id`, `sms_name`, `sms_text`) VALUES
(2, 'Send Estimate PDF Template', 'Hello, [NAME]. This is an automatic message from [company_name_short]. Our estimator visited your property today and sent you an estimate by email to [EMAIL]. Please call us at [office_phone_mask] if you didn\'t receive it or would like to schedule the work. Thank you.'),
(3, 'Estimator- Lead map template', 'Hello, [NAME]. This is an automatic message from [company_name_short].  You requested an estimate for tree services from us and we will be coming today to your property to take a look at your trees. If you have any questions please call us [office_phone_mask]. Thank you.'),
(4, 'Tree Services Schedule for [ADDRESS]', 'Hello, [NAME]. This is an automatic message from [company_name_short]. We scheduled the work at [ADDRESS] for [DATE]. The details were sent to [EMAIL]. If you have any questions please call us at [office_phone_mask]. Thank you.'),
(5, 'New Invoice Sent', 'Hello, [NAME]. This is an automatic message from [company_name_short]. Your invoice for the work at [ADDRESS] for a total of [AMOUNT] has been sent to [EMAIL]. If you have any questions please call us at [office_phone_mask]. Thank you.'),
(6, 'Overdue invoice', 'Hello, [NAME]. This is an automatic message from [company_name_short]. Your invoice for the work at {address} for a total of {amount} is now Overdue.  Please proceed with the payment ASAP. If you have any questions please call us at [office_phone_mask]. Thank you.'),
(7, 'Stump Grinding Schedule for [ADDRESS]', 'Hello, [NAME]. This is an automatic message from [company_name_short]. We scheduled the stump grinding at [ADDRESS] for [DATE]. The details were sent to [EMAIL]. If you have any questions please call us at [office_phone_mask]. Thank you.'),
(8, 'Tree work reschedule', 'Hello, [NAME]. This is an automatic message from [company_name_short]. Unfortunately, the tree work at your at [ADDRESS] for [DATE] has to be rescheduled. We apologize for the inconvenience. The details were sent to [EMAIL]. We will contact you with a new date ASAP. If you have any questions please call us at [office_phone_mask]. Thank you.'),
(9, 'Our crews are on the way', 'Hello, [NAME]. This is an automatic message from [company_name_short]. Our crews are on the way to your property now. They will be arriving in {time} traffic depending. If you have any questions please call us at [office_phone_mask]. Thank you.'),
(10, 'Firewood Delivery for [ADDRESS]', 'Hello, [NAME]. This is an automatic message from [company_name_short]. We scheduled the firewood deliver at [ADDRESS] for [DATE]. The details were sent to [EMAIL]. If you have any questions please call us at [office_phone_mask]. Thank you.');
