<table width="100%" class="table b-a"><tr><td width="25%" class="text-center b-r" valign="middle"><strong>Time</strong></td><td width="75%" class="text-left"><?php echo date("H:i", $start_time) . ' - ' . date("H:i", $end_time); ?></td></tr><tr><td width="25%" class="text-center b-r" valign="middle"><strong>Workorder</strong></td><td width="75%" class="text-left"><a href="<?php echo base_url($workorder_no); ?>" target="_blank"><?php echo $workorder_no; ?></a></td></tr><tr><td width="25%" class="text-center b-r" valign="middle"><strong>Address</strong></td><td width="75%" class="text-left"><?php echo $address; ?></td></tr><tr><td width="25%" class="text-center b-r" valign="middle"><strong>Estimator</strong></td><td width="75%" class="text-left"><?php echo $estimator; ?></td></tr><tr><td class="text-center b-r" style="vertical-align: middle;"><strong>Team</strong></td><td class="text-left"><ul class="m-n" style="padding-left: 15px;"><?php foreach($items as $item) : ?><li><?php if($item['item_id'] == $item['team_leader_user_id']) : ?>* <?php endif; ?>	<?php echo $item['name']; ?></li><?php endforeach; ?></ul></td></tr></table>
